import React, { useState } from 'react';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/YourBotArmy';

const App = () => {
  const [army, setArmy] = useState([]);

  const enlistBot = (botId) => {
    if (!army.includes(botId)) {
      setArmy([...army, botId]);
    }
  };

  const releaseBot = (bot) => {
    setArmy(army.filter(id => id !== bot.id));
  };

  const dischargeBot = async (botId) => {
    try {
      await fetch(`https://botbattlr-hellen-cherotich.vercel.app/${botId}`, {
        method: 'DELETE'
      });
      setArmy(army.filter(id => id !== botId));
    } catch (error) {
      console.error('Error discharging bot:', error);
    }
  };

  return (
    <div>
      <BotCollection enlistBot={enlistBot} />
      <YourBotArmy army={army} releaseBot={releaseBot} dischargeBot={dischargeBot} />
    </div>
  );
};

export default App;
